var db = require('controllers/database');
var mongoose = require('mongoose');
var admins = mongoose.model('admins');

exports.login = function(username,password,callback){
  admins.find({username:username},function(err,data){
    if (data.length != 0) {
      var token = data[0]._id;
      var username = data[0].username;
      var passwordDB = data[0].password;
      if (password == passwordDB) {
        callback({'res':true,'response':"Successfully Admins, login.",'token':token,'username':username});
      } else {
        callback({'res':false,'response':"Error Admins, invalid password."});
      }
    } else {
      callback({'res':false,'response':"Error Drivers, username not exist."});
    }
  });
}
