var db = require('controllers/database');
var mongoose = require('mongoose');
var books = mongoose.model('books');
var advances = mongoose.model('advances');
var drivers = mongoose.model('drivers');
var method	= require('../../route/methods');

exports.getAllBookNow = function(callback){
  books.find({},{_id:1,nameClient:1,nameDriver:1,value:1,date:1},function(err,doc){
		if (doc.length != 0) {
      callback({'res':true,'data':doc});
    } else {
      callback({'res':false});
    }
  });
}

exports.getAllBookAdvance = function(callback){
  advances.find({},{_id:1,username:1,nameDriver:1,dateBook:1,repeat:1},function(err,doc){
		if (doc.length != 0) {
      callback({'res':true,'data':doc});
    } else {
      callback({'res':false});
    }
  });
}

exports.addBookAdvanceWithRepeat = function(token,username,latitude,longitude,date,description,mon,tue,wed,thu,fri,sat,sun,callback){
  var datenew = new Date();
  var xdate = datenew.getDate() + "/"
    + (datenew.getMonth()+1)  + "/"
    + datenew.getFullYear() + " "
    + datenew.getHours() + ":"
    + datenew.getMinutes() + ":"
    + datenew.getSeconds();
  var newBook = new advances({token:token,username:username,latitude:latitude,longitude:longitude,dateBook:date,description:description,
    status:false,repeat:true,date:xdate,tokenDriver:null,nameDriver:null,days:{mon:((mon=='true')?true:false),
      mon:((mon=='true')?true:false),tue:((tue=='true')?true:false),wed:((wed=='true')?true:false),
      thu:((thu=='true')?true:false),fri:((fri=='true')?true:false),sat:((sat=='true')?true:false),
      sun:((sun=='true')?true:false)}});
  newBook.save(function(err){
    if(err){
      callback({'res':false, 'response':"Error Book Advance, you have not added a new book."});
    }else{
      callback({'res':true, 'response':"Successfully Book Advance, you have added a new book."});
    }
  });
}

